import React, { useEffect, useState } from "react";
import axios from "axios";
import { useIntern } from "../context/InternContext";

export default function Leaderboard() {
  const { intern } = useIntern();
  const [interns, setInterns] = useState([]);

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/api/leaderboard/")
      .then((res) => setInterns(res.data))
      .catch((err) => console.error("Error fetching leaderboard", err));
  }, []);

  return (
    <div className="min-h-screen bg-[#0E101C] text-white p-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-blue-400">üèÜ Leaderboard</h1>
        {intern && (
          <p className="mt-2 text-sm text-gray-300">
            Logged in as <span className="font-semibold">{intern.name}</span> ({intern.email})
          </p>
        )}
      </div>

      <div className="overflow-x-auto">
        <table className="w-full bg-[#1B1D2A] rounded-lg shadow-md">
          <thead>
            <tr className="text-left border-b border-gray-700">
              <th className="p-4">Rank</th>
              <th className="p-4">Intern Name</th>
              <th className="p-4">Referral Code</th>
              <th className="p-4">Donations Raised</th>
            </tr>
          </thead>
          <tbody>
            {interns.map((i, index) => (
              <tr key={index} className="border-b border-gray-700 hover:bg-[#252736] transition">
                <td className="p-4">{index + 1}</td>
                <td className="p-4">{i.name}</td>
                <td className="p-4">{i.referral_code}</td>
                <td className="p-4">‚Çπ{i.donations_raised}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
