import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const [intern, setIntern] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/api/intern/")
      .then((res) => {
        console.log("API RESPONSE:", res.data);
        setIntern(res.data);
      })
      .catch((err) => {
        console.error("AXIOS ERROR:", err);
        setError("Failed to fetch intern data.");
      });
  }, []);

  if (error) {
    return <p className="text-red-500 text-center mt-8">{error}</p>;
  }

  if (!intern) {
    return <p className="text-gray-600 text-center mt-8">Loading dashboard...</p>;
  }

  return (
    <div className="max-w-xl mx-auto p-6 mt-10 bg-white rounded-xl shadow-md space-y-6">
      <h2 className="text-2xl font-bold text-indigo-600">Welcome, {intern.name}</h2>

      <div className="space-y-2">
        <p className="text-lg"><span className="font-semibold text-gray-700">Referral Code:</span> {intern.referral_code}</p>
        <p className="text-lg"><span className="font-semibold text-gray-700">Total Donations Raised:</span> â‚¹{intern.donations_raised}</p>
      </div>

      <div>
        <h3 className="text-xl font-semibold text-indigo-500 mb-2">Rewards</h3>
        <ul className="list-disc list-inside space-y-1">
          {intern.rewards.map((reward, idx) => (
            <li key={idx} className="text-gray-800">{reward}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}
