import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const [intern, setIntern] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/api/intern/")
      .then((res) => {
        console.log("API RESPONSE:", res.data);
        setIntern(res.data);
      })
      .catch((err) => {
        console.error("AXIOS ERROR:", err);
        setError("Failed to fetch intern data.");
      });
  }, []);

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="bg-white p-6 rounded-lg shadow-lg text-center text-red-600 font-semibold">
          {error}
        </div>
      </div>
    );
  }

  if (!intern) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="bg-white p-6 rounded-lg shadow-lg text-center text-gray-500">
          Loading dashboard...
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 px-4">
      <div className="w-full max-w-md bg-white p-8 rounded-xl shadow-xl space-y-6">
        <h2 className="text-2xl font-bold text-indigo-600 text-center">
          Welcome, {intern.name}
        </h2>

        <div className="space-y-4">
          <div className="bg-gray-50 rounded-md p-4 shadow-sm">
            <p className="text-sm text-gray-500">Referral Code</p>
            <p className="text-lg font-medium text-gray-800">{intern.referral_code}</p>
          </div>

          <div className="bg-gray-50 rounded-md p-4 shadow-sm">
            <p className="text-sm text-gray-500">Total Donations Raised</p>
            <p className="text-lg font-medium text-green-600">â‚¹{intern.donations_raised}</p>
          </div>
        </div>

        <div>
          <h3 className="text-xl font-semibold text-indigo-500 mb-2">Rewards</h3>
          <ul className="list-disc list-inside space-y-1 text-gray-700">
            {intern.rewards.map((reward, idx) => (
              <li key={idx}>{reward}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
